var vm = require('vm');
var fs = require('fs');

exports.linguaResourceParser = function(path, file) {
    var content = fs.readFileSync(path + file, 'UTF-8');
    content = JSON.parse(content);

    if ( ! /^language\.[a-z-]+\.json$/.test(file) ) {
        new Error("translation file must use the format language.<lang>.json: " + file);
    }

    var translates = {};
    for (var key in content) {
        translates[key] = content[key].value;
    }

    var locale = file.replace('.json', '').replace('language.', '');

    return {
        locale: locale,
        content: translates
    };
};

exports.linguaSentenceParser = function(sentence, arrayParams) {
    if ( arrayParams ) {
        var prefix = "@var";
        for (var i = 0; i < arrayParams.length; i++){
            sentence = sentence.replace(prefix + i, arrayParams[i]);
        }
    }
    return sentence;
}

